mapper.select:
select /* 물성검사계획등록 : 물성검사계획 팝업 조회 [ProcessInspPlanRegMapper.getInspPlanMastPopup] CreatedBy 김범수*/ pm.prdtn_corp_id /*생산법인ID*/ ,pm.fac_id /*공장ID*/ ,pm.insp_plan_no /*검사계획번호*/ ,pm.insp_plan_nm /*검사계획명*/ ,pm.insp_tp_cd /*검사유형코드*/ ,pm.smp_tp_cd /*시료유형코드*/ ,pm.smp_grp_cd /*시료그룹코드*/ ,pm.day_tp_cd /*요일구분코드*/ ,pm.use_yn /*사용여부*/ ,pm.file_seq /*파일SEQ*/ ,pm.remark /*비고*/ ,cc1.cd_val_kr_nm as insp_tp_nm /*검사구분명*/ ,sm1.smp_nm as smp_tp_nm /*시료유형명*/ ,sm2.smp_nm as smp_grp_nm /*시료그룹명*/ ,cc2.cd_val_kr_nm as day_tp_nm /*요일명*/ from tbl_qm_insp_plan_mast pm , tbl_cmm_cd cc1 , tbl_cmm_cd cc2 , tbl_qm_smp_mgmt sm1 , tbl_qm_smp_mgmt sm2 where pm.prdtn_corp_id = cc1.prdtn_corp_id(+) and pm.insp_tp_cd = cc1.cd_val(+) and cc1.cd_tp_id(+) = 'INSP_TP' and cc1.use_yn(+) = 'Y' and pm.prdtn_corp_id = cc2.prdtn_corp_id(+) and pm.day_tp_cd = cc2.cd_val(+) and cc2.cd_tp_id(+) = 'DAY_TP' and cc2.use_yn(+) = 'Y' and pm.prdtn_corp_id = sm1.prdtn_corp_id(+) and pm.fac_id = sm1.fac_id(+) and pm.smp_tp_cd = sm1.smp_cd(+) and sm1.data_tp_cd(+) = 'SMP_TYPE' and sm1.use_yn(+) = 'Y' and pm.prdtn_corp_id = sm2.prdtn_corp_id(+) and pm.fac_id = sm2.fac_id(+) and pm.smp_grp_cd = sm2.smp_cd(+) and sm2.data_tp_cd(+) = 'SMP_GROUP' and sm2.use_yn(+) = 'Y' and pm.prdtn_corp_id = #{prdtnCorpId} /*생산법인ID*/ and pm.fac_id = #{facId} /*공장ID*/ and pm.use_yn = 'Y' /*사용여부*/ and pm.insp_plan_nm like '%'||#{inspPlanNm}||'%'
and pm.insp_tp_cd = #{inspTpCd}
and pm.day_tp_cd = #{dayTpCd}
